const showTab=e=>{const t=e.getAttribute("data-tab"),n=document.querySelectorAll(".tabs ul li"),s=document.querySelectorAll(".tabs-footer ul li"),o=document.querySelectorAll(".tab");n.forEach(e=>{e.getAttribute("data-tab")===t?e.classList.add("active"):e.classList.remove("active")}),s.forEach(e=>{e.getAttribute("data-tab")===t?e.classList.add("active-footer"):e.classList.remove("active-footer")}),o.forEach(e=>{e.getAttribute("data-tab")===t?e.classList.add("show"):e.classList.remove("show")})}